import './App.css';



const App = () => {
  const [counter , setCounter] = useState(0)

  return (
    <div className="App">
     <button onClick={() => {setCounter((prevCount)=>{prevCount = prev})}}>-</button>
     <h2>{counter}</h2>
     <button>+</button>
    </div> 


  );
}

export default App;
