import { useEffect } from "react";
import React from 'react';
import './App.css';
import searchIcon from './Search.svg';

//2eecfd57
const API_URL = 'https://www.omdbapi.com/?apikey=2eecfd57'

const movie1 = {
    "Title": "Team Thor: Part 2",
    "Year": "2017",
    "imdbID": "tt6599818",
    "Type": "movie",
    "Poster": "https://m.media-amazon.com/images/M/MV5BODczODMwOTgtODhkOC00YjFiLWIzYmUtZTI3NThhZDE1NDhkXkEyXkFqcGdeQXVyNTE1NjY5Mg@@._V1_SX300.jpg"
}
const App =  () => {
    const searchMovie = async (title) => {
        const response = await fetch (`${API_URL}&s=${title}`);
        const data = await response.json();

        console.log(data.Search);
    }
    useEffect(()=>{
        searchMovie('thor');
    },[])

    return (
        <div className="app">
        <h1>MovieLand</h1>
        <div className="search">
            <input
            placeholder="Search for a movie"
            value="superman"
            onChange={()=>{}}
            />
            <img
            src= {searchIcon}
            alt="search"
            onClick={()=>{}}
            />
        </div>
        <div className="container">
            <div className="movie">
                <div >
                    <p>{movie1.Year}</p>
                </div>
                <div>
                    <img src= {movie1.Poster !== 'N/A' ? movie1.Poster : "https://via0."} />
                </div>
                <div>
                    <span>{movie1.Type}</span>
                    <h3> {movie1.Title} </h3>
                </div>

            </div>
        </div>

        </div>
    );
}
export default App;
